import './App.css';
import React, {Component} from 'react';
import ReactMapGL, {Source, Layer} from 'react-map-gl';
import illinois_counties from './illinois_counties.json';
import {baseLayer, selected} from './CountyLayer';

class App extends Component {
  state = {
    data: null,
    filter: ['in', 'COUNTY', ''],
    viewport: {
      latitude: 40.150196,
      longitude: -89.367848, 
      zoom: 6,
      width: "50vw",
      height: "100vh"
    },
    x: null,
    y: null,
    hoveredFeature: null
  }

  componentDidMount() {
    // API Call to get data
    this.setState({data: this.addSelected(illinois_counties)})
  }

  addSelected(featureCollection) {
    const {features} = featureCollection;
    return {
      type: 'FeatureCollection',
      features: features.map(f => {
        const properties = {
          ...f.properties,
          selected: false
        };
        return {...f, properties};
      })
    };
  }


  onHover = event => {
    const {
      features,
      srcEvent: {offsetX, offsetY}
    } = event;
    const hoveredFeature = features && features.find(f => f.layer.id === 'county');
    var countyId = '';
    if (hoveredFeature) {
      countyId = hoveredFeature.properties.COUNTY;
    }
    this.setState({hoveredFeature: hoveredFeature, x: offsetX, y: offsetY, filter: ['in', 'COUNTY', countyId]});
    console.log(this.state);
  };

  renderTooltip() {
    const {hoveredFeature, x, y} = this.state;

    return (
      hoveredFeature && (
        <div className="tooltip" style={{left: x, top: y}}>
          <div>County: {hoveredFeature.properties.NAME}</div>
          <div>FIPS: {hoveredFeature.properties.COUNTY}</div>
        </div>
      )
    );
  }

  render() {
    return (
      <div className="App">
        <ReactMapGL
          {...this.state.viewport}
          mapboxApiAccessToken={"pk.eyJ1IjoiZWdhYnJpZWxzZSIsImEiOiJja2d2ZDZua2QwMWI3M2JwajA0Z3lqbDdmIn0.sAaMKjFMEglTFxZwKyU75Q"}
          onViewportChange={(newViewport) => this.setState({viewport: newViewport})}
          onHover={this.onHover}
        >
          <Source id="counties" type="geojson" data={this.state.data}>
            <Layer {...baseLayer}></Layer>
            <Layer {...selected} filter={this.state.filter}></Layer>
          </Source>
          {this.renderTooltip()}
        </ReactMapGL>
      </div>
    );
  }
}

export default App;
